sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.journey_template_stages_task_widget = $sp.getWidget('jny_template_card');
    data.redirected_from_task_list = $sp.getParameter("focus_on_unpublished") === 'true';
})();
