function ($scope, $location, spAriaFocusManager) {
  "use strict";

  // OPTIONAL: open form when a row is clicked (if you wire it from the template or row action)
  $scope.openRecord = function (row) {
    var params = angular.extend({}, $location.search(), {
      table: $scope.data.table,
      sys_id: row.sys_id,
      view:  $scope.data.view || 'sp'
    });
    $location.search(params);
    spAriaFocusManager.navigateToLink($location.absUrl());
  };
}

