(function () {
  'use strict';

  // --- Resolve inputs (table, fields, filter) ---
  // Prefer instance options; fall back to URL or sensible defaults
  data.table  = (input && input.table)  || options.table  || $sp.getParameter('table')  || 'x_cpszo_spoke_customer_profile';
  data.view   = options.view || $sp.getParameter('view') || 'sp';

  // Ensure fields include the risk band column (adjust the API name if needed)
  var fieldList = (options.fields || 'name,number,u_risk_band')
                    .replace(/\s/g, ''); // strip spaces
  data.fields = fieldList;

  // Accept sysparm_query / filter from options or URL
  data.filter = options.filter || $sp.getParameter('sysparm_query') || '';

  // If the table is missing, stop early so the HTML can show the warning
  data.invalid_table = !data.table;
  data.table_label = data.table;

  if (data.invalid_table)
    return;

  // --- Build params for the inner OOTB Data Table widget ---
  var widgetParams = {
    table:  data.table,
    fields: data.fields,
    view:   data.view,
    filter: data.filter,

    // ðŸ‘‡ This is the key: map field value â†’ CSS class
    field_styles: {
      // use your actual field name here (u_risk_band is from your screenshots)
      u_risk_band: {
        "High":   { "class": "risk-badge risk-high" },
        "Medium": { "class": "risk-badge risk-medium" },
        "Low":    { "class": "risk-badge risk-low" }
      }
    }
  };

  // Render the standard Data Table with our styling options
  // (Identifier is "widget-data-table" on most instances; if your clone used a different id,
  // keep that same id here.)
  data.dataTableWidget = $sp.getWidget('widget-data-table', widgetParams);
})();
