sp_widget.client_script:
function($scope, $location, spUtil) {
  var c = this;

  // Read sysparm_customer_id from URL
  c.customerId = $location.search().sysparm_customer_id;

  c.offerName = '';
  c.description = '';

  c.submitOffer = function() {
    var ga = new GlideAjax('CreateOfferScriptedProcessor');
    ga.addParam('sysparm_name', 'createOffer');
    ga.addParam('sysparm_customer_id', c.customerId);
    ga.addParam('sysparm_offer_name', c.offerName);
    ga.addParam('sysparm_description', c.description);
    ga.getXMLAnswer(function(response) {
      spUtil.addInfoMessage('Offer created successfully!');
    });
  };
}

sp_widget.script:
(function() {
  data.record = {};
  data.sys_id = '';
  data.error = '';

  if (!input || !input.number) {
    data.error = "No number parameter provided.";
    return;
  }

  var gr = new GlideRecord('x_offer_tracker'); // Replace with actual table name
  if (gr.get('number', input.number)) {
    data.sys_id = gr.getUniqueValue();

    var fields = gr.getFields();
    for (var i = 0; i < fields.size(); i++) {
      var field = fields.get(i);
      data.record[field.getName()] = gr.getValue(field.getName());
    }
  } else {
    data.error = "Record not found for number: " + input.number;
  }
})();
