sp_widget.client_script:
function($scope) {
  var c = this;

  c.createOffer = function() {
    window.open('/x_cpszo_spoke_offer_tracker.do?sys_id=-1', '_blank');
  };
	if (window.opener) {
  window.opener.location.href = '/credit_ops'; // Redirect original tab
  window.close(); // Close current tab
}

  c.createReminder = function() {
    window.open('/x_cpszo_spoke_reminder_log_list.do', '_blank');
  };
	if (window.opener) {
  window.opener.location.href = '/credit_ops'; // Redirect original tab
  window.close(); // Close current tab
}

}

sp_widget.css:
/* Panel styling */
.panel-default {
  background-color: #ffffff;
  padding: 16px;
  border-radius: 8px;
}

/* Base button style */
.btn {
  font-size: 15px;
  font-weight: 500;
  padding: 10px 20px;
  border-radius: 6px;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.2s ease;
  outline: none;
}

/* Create Offer button */
.btn-primary {
  background-color: #4e54c8;
  color: #fff;
}

