sp_widget.script:
(function() {
    data.offerTracker = [];
    data.reminderLog = [];

    var offerGR = new GlideRecord('x_cpszo_spoke_offer_tracker');
    offerGR.orderByDesc('sys_created_on');
    //offerGR.setLimit(10); // Set limit before query
    offerGR.query();
    while (offerGR.next()) {
        data.offerTracker.push({
            sys_id: offerGR.getValue('sys_id'),
            number: offerGR.getDisplayValue('number'),
            acceptance_date: offerGR.getDisplayValue('acceptance_date'),
            offer_status: offerGR.getValue('offer_status'),
            delinquency_status: offerGR.getValue('delinquency_status'),
            installment_amount: offerGR.getDisplayValue('installment_amount')
        });
    }

    var reminderGR = new GlideRecord('x_cpszo_spoke_reminder_log');
    reminderGR.orderByDesc('sys_created_on');
   // reminderGR.setLimit(10); // Set limit before query
    reminderGR.query();
    while (reminderGR.next()) {
        data.reminderLog.push({
            sys_id: reminderGR.getValue('sys_id'),
            number: reminderGR.getDisplayValue('number'),
            preferred_channel: reminderGR.getValue('preferred_channel'),
            reminder_type: reminderGR.getValue('reminder_type'),
            reminder_schedule: reminderGR.getDisplayValue('reminder_schedule'),
            reminder_message: reminderGR.getValue('reminder_message'),
            last_contact_date: reminderGR.getDisplayValue('last_contact_date')
        });
    }
})();
