sp_widget.script:
(function() {
  data.loading = true;
  data.customer = null;
  data.error = null;

  try {
    // Priority: input.number (from client update) > URL ?number > option
    var number = (input && input.number) ||
                 $sp.getParameter('number') ||
                 (options && options.number);

    var gr = new GlideRecord('x_cpszo_spoke_customer_profile');
    if (number) {
      gr.addQuery('number', number);
      gr.query();
      if (gr.next()) {
        data.customer = {
          days_since_last_payment_display: gr.getDisplayValue('days_since_last_payment'),
          days_since_last_payment: gr.getValue('days_since_last_payment')
        };
      } else {
        data.error = 'Record not found for number: ' + number;
      }
    } else {
      // Fallback: latest record
      gr.orderByDesc('sys_updated_on');
      gr.setLimit(1);
      gr.query();
      if (gr.next()) {
        data.customer = {
          days_since_last_payment_display: gr.getDisplayValue('days_since_last_payment'),
          days_since_last_payment: gr.getValue('days_since_last_payment')
        };
      }
    }
  } catch (e) {
    data.error = (e && e.message) ? e.message : (e + '');
  } finally {
    data.loading = false;
  }
})();
