sp_widget.client_script:
function($scope, $location) {
	var c = this;
  $scope.selectedCustomer = null;
  $scope.sortColumn = 'number';
  $scope.reverseSort = false;

  $scope.currentPage = 1;
  $scope.pageSize = 10;
  $scope.selectCustomer = function(customer) {
		if(!customer) return;
    $scope.selectedCustomer = customer;
    $location.search('table', 'CustomerProfile');
    $location.search('number', customer.number);
		$location.search('sys_id', customer.sys_id);

  };

  $scope.getRiskStyle = function(risk) {
    switch (risk.toLowerCase()) {
      case 'high':
        return { 'color': 'red', 'font-weight': 'bold' };
      case 'medium':
        return { 'color': 'orange', 'font-weight': 'bold' };
      case 'low':
        return { 'color': 'green', 'font-weight': 'bold' };
      default:
        return {};
    }
  };
	 c.createCustProfile = function() {
    window.open('/x_cpszo_spoke_customer_profile.do', '_blank');
  };
	if (window.opener) {
  window.opener.location.href = '/credit_ops'; // Redirect original tab
  window.close(); // Close current tab
}


}

