sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

 var msg = inputs.fallback_message || '';
 
  var rem = new GlideRecord('x_cpszo_spoke_reminder_log');
  if (rem.get(inputs.reminder_sys_id)) {
    // pick template by channel & active, prefer lowest u_priority
    var tplGR = new GlideRecord('u_notification_config');
tplGR.addQuery('u_channel', inputs.channel);  // e.g., SMS/Email/WhatsApp
    tplGR.addQuery('u_active', true);
    tplGR.orderBy('u_priority');   // 1 = highest priority
    tplGR.setLimit(1);
    tplGR.query();
 
    if (tplGR.next()) {
      var nu = new NotificationUtils();
      var tpl = tplGR.u_template.getDisplayValue();   // HTML/Journal field
      // Build variables for the template
      var data = {
        customer_name: rem.number.getDisplayValue(),                 // reference display
        due_date: rem.u_reminder_schedule.getDisplayValue(),         // date/time display
        amount: '' + (rem.u_amount_due || '')                        // if you have it
      };
      msg = nu.renderTemplate(tpl, data);
    } else if (rem.u_reminder_message) {
      msg = rem.u_reminder_message + '';
    }
  }
 
  outputs.message = msg;
})(inputs, outputs);

