(function execute(inputs, outputs) {
  var dh  = new DemoHelpers();
  var n   = inputs.number  || inputs.Number  || inputs.customer_id || '';
var ch = inputs.channel || inputs.Channel || '';
  var msg = inputs.message || inputs.Message || '';
 
  var nid = dh.sendMockNotification(n, ch, msg);
 
  if (inputs.reminder_sys_id) {
    var r = new GlideRecord('x_cpszo_spoke_reminder_log');
    if (r.get(inputs.reminder_sys_id)) {
      r.reminder_status     = 'Sent';
      r.last_contact_date   = new GlideDateTime();
      r.notification_sys_id = nid;
      r.update();
    }
  }
 
  outputs.notification_sys_id = nid;
  outputs.status = 'Sent';
})(inputs, outputs);

