sys_script_client.script:
function onLoad() {
    (function executeRule(current, gForm, gUser, gSNC) {
        function getParameterByName(name) {
            var url = window.location.href;
            name = name.replace(/[\[\]]/g, '\\$&');
            var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');
            var results = regex.exec(url);
            if (!results) return null;
            if (!results[2]) return '';
            return decodeURIComponent(results[2].replace(/\+/g, ' '));
        }

        var customerNumber = getParameterByName('number');
        if (!customerNumber) return;

        var ga = new GlideAjax('GetCustomerProfileSysId');
        ga.addParam('sysparm_name', 'getSysIdFromNumber');
        ga.addParam('sysparm_number', customerNumber);
        ga.getXMLAnswer(function(response) {
            if (response) {
                gForm.setValue('number', response);
            }
        });
    })(current, gForm, gUser, gSNC);

}
