var GetCustomerProfileSysId = Class.create();
GetCustomerProfileSysId.prototype = Object.extendsObject(AbstractAjaxProcessor, {
  getSysIdFromNumber: function() {
    var number = this.getParameter('sysparm_number');
    if (!number) return '';

    var gr = new GlideRecord('x_cpszo_spoke_customer_profile');
    gr.addQuery('number', number); // âœ… Replace with actual field name
    gr.query();

    if (gr.next()) {
      return gr.getUniqueValue(); // Return sys_id
    }

    return ''; // Not found
  }
});
