sys_ui_action.script:
(function() {
    try {
        // Construct the message
        var msg = 'Reminder: Payment due ' + (current.reminder_schedule ? current.reminder_schedule.getDisplayValue() : '');

        // Save the message back to the record
        //current.setValue('reminder_message', msg);
		var dt = new GlideDateTime();

        // Create a new notification record (replace with actual table name)
        var notif = new GlideRecord('x_cpszo_spoke_reminder_log'); // Replace with actual table name
        notif.initialize();
        notif.setValue('reminder_message', msg);
        notif.setValue('reminder_type', current.reminder_type);
        notif.setValue('reminder_schedule', current.reminder_schedule);
        notif.setValue('preferred_channel', current.preferred_channel);
        notif.setValue('number', current.number);
		notif.setValue('last_contact_date', dt.getValue());
        var id = notif.insert();

        if (!id) {
            gs.addErrorMessage('Notification failed to send.');
            return;
        }

        // Update current record fields
        current.setValue('reminder_status', 'Sent');
        //current.setValue('last_contact_date', new GlideDateTime().getValue());
        current.setValue('notification_sys_id', id);
        current.setWorkflow(false); // Optional: prevent triggering workflows
        current.update();

        gs.addInfoMessage('Reminder resent.');
    } catch (e) {
        gs.addErrorMessage('Error occurred: ' + e.toString());
    }
})();
