(function process(request, response) {
    var customerId = request.queryParams.customer_id;

    if (!customerId) {
        response.setStatus(400);
        response.setBody({ error: 'Missing customer_id parameter' });
        return;
    }

    var gr = new GlideRecord('x_cpszo_spoke_customer_profile'); // Replace with your actual table name if different
    gr.addQuery('number', customerId); // Using 'number' field instead of 'customer_id'
    gr.query();

    if (gr.next()) {
        var result = {
            customer_id: customerId,
            credit_behavior: gr.getValue('credit_behaviour')
        };
        response.setStatus(200);
        response.setBody(result);
    } else {
        response.setStatus(404);
        response.setBody({ error: 'Customer not found' });
    }
})(request, response);
