sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;

    // Lookup customer profile by customer_id
    var customerGR = new GlideRecord('x_cpszo_spoke_customer_profile');
    customerGR.addQuery('number', body.number); // body.number = 'CUST-1001'
    customerGR.query();

    if (!customerGR.next()) {
        response.setStatus(400);
        response.setBody({ error: 'Invalid customer_id: ' + body.number });
        return;
    }

    // Create Offer Tracker record
    var gr = new GlideRecord('x_cpszo_spoke_offer_tracker');
    gr.initialize();

    gr.number = customerGR.getUniqueValue(); // Set reference using Sys ID
    gr.offer_type = body.offer_type;
    gr.installment_amount = body.installment_amount;
    gr.acceptance_date = body.acceptance_date;
    gr.delinquency_status = body.delinquency_status;

    var newSysId = gr.insert();

    response.setStatus(201);
    response.setBody({
        message: 'Offer Tracker record created successfully',
        sys_id: newSysId
    });
})(request, response);
