sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var requestBody = request.body.data || {}; 

    // Expected keys: customer_id, channel, message 
    var customerId = requestBody.number; 
    var channel = requestBody.channel || 'SMS'; 
    var message = requestBody.message || ''; 
    var notif = new GlideRecord('x_cpszo_spoke_notification_preview'); 

    notif.initialize(); 
    notif.customer = customerId; 
    notif.channel = channel; 
    notif.message = message; 
    notif.status = 'Sent'; // for demo, mark as Sent 
    notif.sent_at = new GlideDateTime(); 

    var nid = notif.insert(); 
    var il = new GlideRecord('x_cpszo_spoke_integration_log'); 
    il.initialize(); 
    il.timestamp = new GlideDateTime(); 
    il.interface = 'Mock Notification POST'; 
    il.payload = JSON.stringify(requestBody); 
    il.status = 'OK'; 
    il.response = JSON.stringify({sent_record_sys_id: nid}); 
    il.insert(); 
    response.setStatus(201); 
    response.setBody({status: 'sent', record_sys_id: nid});

})(request, response);
